package programSecond;

public class FactorialNum {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 3;
			int fact = 1;
			for(int i =1; i<=num;i++) {
				fact *= i;
			}
		    System.out.println("factorial of number :"+fact);
			
			
			}
		
				

	}


